import pandas as pd

def load_security_data(file_path):
    df = pd.read_excel(file_path, header=None)  
    security_codes = df.iloc[:, 0].astype(str).str.strip() 
    security_ids = df.iloc[:, 1].astype(str).str.strip()   
    
    security_dict = dict(zip(security_codes, security_ids))
    
    return security_dict

def print_security_ids(security_dict, security_code_list):
    for code in security_code_list:
        code = str(code).strip()  
        if code in security_dict:
            print(f"BSE:{security_dict[code]}")
        else:
            print(f"BOM:{code}")

